import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import PlayForm from '../my-broadway-app/src/components/PlayForm';
import PlayList from '../my-broadway-app/src/components/PlayList';
import Wishlist from './pages/Wishlist';
import SearchBar from '../my-broadway-app/src/components/SearchBar';

function App() {
  const [plays, setPlays] = useState([]);
  const [wishlist, setWishlist] = useState([]);

  useEffect(() => {
    fetchPlays();
  }, []);

  const fetchPlays = () => {
    fetch('/api/plays')
      .then(response => response.json())
      .then(data => setPlays(data));
  };

  const addPlay = newPlay => {
    fetch('/api/plays', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(newPlay)
    })
      .then(response => response.json())
      .then(data => setPlays([...plays, data]));
  };

  const deletePlay = id => {
    fetch(`/api/plays/${id}`, {
      method: 'DELETE'
    })
      .then(() => setPlays(plays.filter(play => play.id !== id)));
  };

  const addToWishlist = play => {
    setWishlist([...wishlist, play]);
  };

  const removeFromWishlist = id => {
    setWishlist(wishlist.filter(play => play.id !== id));
  };

  return (
    <Router>
      <div>
        <Navbar />
        <Switch>
          <Route path="/wishlist">
            <Wishlist wishlist={wishlist} removeFromWishlist={removeFromWishlist} />
          </Route>
          <Route path="/plays">
            <PlayList plays={plays} addToWishlist={addToWishlist} deletePlay={deletePlay} />
          </Route>
          <Route path="/">
            <PlayForm addPlay={addPlay} />
            <SearchBar plays={plays} setPlays={setPlays} />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
