import React from "react";

function PlayCard({ play, setPlays }) {
  const handleDelete = () => {
    fetch(`http://localhost:6001/plays/${play.id}`, {
      method: "DELETE",
    })
      .then(() => {
        setPlays(plays => plays.filter(p => p.id !== play.id));
      });
  };

  return (
    <li className="card">
      <img src={play.image} alt={play.name} />
      <h4>{play.name}</h4>
      <p>Open Date: {play.open_date}</p>
      <p>Performance Count: {play.performance_count}</p>
      <p>Theater: {play.theater}</p>
      <p>Director: {play.director_name}</p>
      <p>{play.short_description}</p>
      <button onClick={handleDelete}>Delete Play</button>
    </li>
  );
}

export default PlayCard;
