import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import PlayForm from './components/PlayForm';
import PlayList from './components/PlayList';
import Wishlist from './pages/Wishlist';
import SearchBar from './components/SearchBar';
import { getPlays, getWishlist, addToWishlist, removeFromWishlist, addPlay, deletePlay } from './utils/data';

function App() {
  const [plays, setPlays] = useState([]);
  const [wishlist, setWishlist] = useState([]);

  useEffect(() => {
    fetchInitialData();
  }, []);

  const fetchInitialData = () => {
    setPlays(getPlays());
    setWishlist(getWishlist());
  };

  const handleAddPlay = (newPlay) => {
    const createdPlay = addPlay(newPlay);
    setPlays([...plays, createdPlay]);
  };

  const handleDeletePlay = (id) => {
    deletePlay(id);
    setPlays(plays.filter((play) => play.id !== id));
  };

  const handleAddToWishlist = (play) => {
    addToWishlist(play);
    setWishlist([...wishlist, play]);
  };

  const handleRemoveFromWishlist = (id) => {
    removeFromWishlist(id);
    setWishlist(wishlist.filter((play) => play.id !== id));
  };

  return (
    <Router>
      <div>
        <Navbar />
        <Routes>
          <Route path="/wishlist" element={<Wishlist wishlist={wishlist} removeFromWishlist={handleRemoveFromWishlist} />} />
          <Route path="/current-plays" element={<PlayList plays={plays.filter(play => play.types === 'current')} addToWishlist={handleAddToWishlist} deletePlay={handleDeletePlay} />} />
          <Route path="/upcoming-plays" element={<PlayList plays={plays.filter(play => play.types === 'upcoming')} addToWishlist={handleAddToWishlist} deletePlay={handleDeletePlay} />} />
          <Route path="/" element={
            <>
              <PlayForm addPlay={handleAddPlay} />
              <SearchBar plays={plays} setPlays={setPlays} />
            </>
          } />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
