import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import PlayForm from './components/PlayForm';
import PlayList from './components/PlayList';
import Wishlist from './pages/Wishlist';
import SearchBar from './components/SearchBar';
import Navbar from './components/Navbar';

class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false };
  }

  componentDidCatch(error, errorInfo) {
    // Log the error or display a fallback UI
    console.error(error, errorInfo);
    this.setState({ hasError: true });
  }

  render() {
    if (this.state.hasError) {
      // Fallback UI
      return <h1>Something went wrong.</h1>;
    }

    return this.props.children;
  }
}

function App() {
  const [plays, setPlays] = useState([]);
  const [wishlist, setWishlist] = useState([]);

  useEffect(() => {
    fetchPlays();
  }, []);

  const fetchPlays = () => {
    fetch('/api/plays')
      .then(response => response.json())
      .then(data => setPlays(data));
  };

  const addPlay = newPlay => {
    fetch('/api/plays', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(newPlay)
    })
      .then(response => response.json())
      .then(data => setPlays([...plays, data]));
  };

  const deletePlay = id => {
    fetch(`/api/plays/${id}`, {
      method: 'DELETE'
    })
      .then(() => setPlays(plays.filter(play => play.id !== id)));
  };

  const addToWishlist = play => {
    setWishlist([...wishlist, play]);
  };

  const removeFromWishlist = id => {
    setWishlist(wishlist.filter(play => play.id !== id));
  };

  return (
    <Router>
      <div>
        <Navbar />
        <Routes>
          <Route path="/wishlist">
            <Wishlist wishlist={wishlist} removeFromWishlist={removeFromWishlist} />
          </Route>
          <Route path="/plays">
            <PlayList plays={plays} addToWishlist={addToWishlist} deletePlay={deletePlay} />
          </Route>
          <Route path="/">
            <PlayForm addPlay={addPlay} />
            <SearchBar plays={plays} setPlays={setPlays} />
          </Route>
        </Routes>
      </div>
    </Router>
  );
}

export default App;
