import React, { useState } from 'react';

function PlayForm({ addPlay }) {
  const [name, setName] = useState('');
  const [image, setImage] = useState('');
  const [openDate, setOpenDate] = useState('');
  const [performanceCount, setPerformanceCount] = useState('');
  const [theater, setTheater] = useState('');
  const [directorName, setDirectorName] = useState('');
  const [shortDescription, setShortDescription] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    const newPlay = {
      name,
      image,
      open_date: openDate,
      performance_count: performanceCount,
      theater,
      director_name: directorName,
      short_description: shortDescription,
    };
    addPlay(newPlay);
    clearForm();
  };

  const clearForm = () => {
    setName('');
    setImage('');
    setOpenDate('');
    setPerformanceCount('');
    setTheater('');
    setDirectorName('');
    setShortDescription('');
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} />
      <input type="text" placeholder="Image URL" value={image} onChange={(e) => setImage(e.target.value)} />
      <input type="date" placeholder="Open Date" value={openDate} onChange={(e) => setOpenDate(e.target.value)} />
      <input type="number" placeholder="Performance Count" value={performanceCount} onChange={(e) => setPerformanceCount(e.target.value)} />
      <input type="text" placeholder="Theater" value={theater} onChange={(e) => setTheater(e.target.value)} />
      <input type="text" placeholder="Director Name" value={directorName} onChange={(e) => setDirectorName(e.target.value)} />
      <textarea placeholder="Short Description" value={shortDescription} onChange={(e) => setShortDescription(e.target.value)}></textarea>
      <button type="submit">Add Play</button>
    </form>
  );
}

export default PlayForm;
