import React, { useState } from 'react';

function PlayCard({ play, addToWishlist, deletePlay, removeFromWishlist }) {
  const [isInWishlist, setIsInWishlist] = useState(false);

  const handleAddToWishlist = () => {
    addToWishlist(play);
    setIsInWishlist(true);
  };

  const handleRemoveFromWishlist = () => {
    removeFromWishlist(play.id);
    setIsInWishlist(false);
  };

  const handleDelete = () => {
    deletePlay(play.id);
  };

  return (
    <div>
      <img src={play.image} alt={play.name} />
      <h3>{play.name}</h3>
      <p>Open Date: {play.openDate}</p>
      <p>Performance Count: {play.performanceCount}</p>
      <p>Theater: {play.theater}</p>
      <p>Director: {play.directorName}</p>
      <p>{play.shortDescription}</p>
      {isInWishlist ? (
        <button onClick={handleRemoveFromWishlist}>Remove from Wishlist</button>
      ) : (
        <button onClick={handleAddToWishlist}>Add to Wishlist</button>
      )}
      <button onClick={handleDelete}>Delete Play</button>
    </div>
  );
}

export default PlayCard;
