import React, { useState, useEffect, useCallback } from "react";
import PlayList from "./PlayList";
import Search from "./Search";

function Wishlist() {
  const [wishlistPlays, setWishlistPlays] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  const fetchWishlistPlays = useCallback(async () => {
    try {
      const response = await fetch("http://localhost:6001/wishlist");
      const data = await response.json();
      setWishlistPlays(data);
    } catch (error) {
      console.error("Error fetching wishlist plays:", error);
    }
  }, []);

  useEffect(() => {
    fetchWishlistPlays();
  }, [fetchWishlistPlays]);

  const filteredPlays = wishlistPlays.filter(play =>
    play.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <main>
      <Search setSearchTerm={setSearchTerm} />
      <PlayList plays={filteredPlays} setPlays={setWishlistPlays} />
    </main>
  );
}

export default Wishlist;
